#################################################
# Get now date string.
#################################################
function now_date() {
    format=$1
    if [[ "${format}" ]]; then
        now=$(date +"${format}")
    else
        now=$(date +"%Y%m%d_%H%M%S")
    fi

    echo "${now}"
}

#################################################
# Basic log function.
# ex: [2021/08/15 19:16:10]
#################################################
function echo_log() {
    now=$(date +"[%Y/%m/%d %H:%M:%S]")
    echo -e "\033[1;$1m${now}$2\033[0m"
}

#################################################
# Debug log message.
#################################################
function msg_debug() {
    echo_log 30 "[Debug] ====> $*"
}

#################################################
# Error log message.
#################################################
function msg_error() {
    echo_log 31 "[Error] ====> $*"
}

#################################################
# Success log message.
#################################################
function msg_success() {
    echo_log 32 "[Success] ====> $*"
}

#################################################
# Warning log message.
#################################################
function msg_warn() {
    echo_log 33 "[Warning] ====> $*"
}

#################################################
# Information log message.
#################################################
function msg_info() {
    echo_log 34 "[Info] ====> $*"
}
